function init(){container=document.createElement("div");document.getElementById("lyrics").appendChild(container);window.addEventListener("resize",onWindowResize,false);camera=new THREE.Camera(75,window.innerWidth/window.innerHeight,1,3000);camera.position.z=1000;scene=new THREE.Scene();var c=Math.PI*2;var a=function(d){d.beginPath();d.arc(0,0,1,0,c,true);d.closePath();d.fill()};group=new THREE.Object3D();scene.addObject(group);for(var b=0;b<1000;b++){particle=new THREE.Particle(new THREE.ParticleCanvasMaterial({color:16724787,program:a}));particle.position.x=Math.random()*2000-1000;particle.position.y=Math.random()*2000-1000;particle.position.z=Math.random()*2000-1000;particle.scale.x=particle.scale.y=Math.random()*10+5;group.addChild(particle)}renderer=new THREE.CanvasRenderer();renderer.setSize(window.innerWidth,window.innerHeight);container.appendChild(renderer.domElement);document.addEventListener("mousemove",onDocumentMouseMove,false);document.addEventListener("touchstart",onDocumentTouchStart,false);document.addEventListener("touchmove",onDocumentTouchMove,false)}function onDocumentMouseMove(a){mouseX=a.clientX-windowHalfX;mouseY=a.clientY-windowHalfY}function onDocumentTouchStart(a){if(a.touches.length==1){a.preventDefault();mouseX=a.touches[0].pageX-windowHalfX;mouseY=a.touches[0].pageY-windowHalfY}}function onDocumentTouchMove(a){if(a.touches.length==1){a.preventDefault();mouseX=a.touches[0].pageX-windowHalfX;mouseY=a.touches[0].pageY-windowHalfY}}function animate(){requestAnimationFrame(animate);render()}function render(){camera.position.x+=(mouseX-camera.position.x)*0.05;camera.position.y+=(-mouseY-camera.position.y)*0.05;group.rotation.x+=0.01;group.rotation.y+=0.02;renderer.render(scene,camera)}function onWindowResize(c){var b=window.innerWidth,a=window.innerHeight;camera.aspect=b/a;camera.updateProjectionMatrix();renderer.setSize(b,a);renderer.domElement.style.width=window.innerWidth+"px";renderer.domElement.style.height=window.innerHeight+"px"};